/* eslint-disable */
const sdk = require("postman-collection"),
  convert = require("./lib/index").convert,
  request = new sdk.Request({
    method: "GET",
    header: [],
    url: {
      raw: "https://postman-echo.com/get?query={{value1}}",
      host: ["https://postman-echo.com"],
      path: ["get"],
      query: [
        {
          key: "query",
          value: "{{value1}}",
        },
      ],
    },
  }),
  options = {};

convert(request, options, function (error, snippet) {
  if (error) {
    console.log(error);
    process.exit(1);
  }

  console.log(snippet);
});
